CONFIDENCE_THRESHOLD = 0.4  # –ù–∞–ø—Ä–∏–º–µ—Ä, 0.5 –∏–ª–∏ 50%

async def detect(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    try:
        photo = update.message.photo[-1]
        photo_file = await photo.get_file()
        output_dir = os.getenv('IMAGE_OUTPUT_DIR', '/Users/uladzislauyermakou/Desktop/FINAL/YOLO-final/images')
        os.makedirs(output_dir, exist_ok=True)

        # –°–æ–∑–¥–∞–µ–º —É–Ω–∏–∫–∞–ª—å–Ω–æ–µ –∏–º—è –¥–ª—è —Ñ–∞–π–ª–∞
        photo_path = os.path.join(output_dir, f"detect_image_{update.message.message_id}.jpg")
        await photo_file.download_to_drive(photo_path)

        # –í—ã–ø–æ–ª–Ω–µ–Ω–∏–µ –¥–µ—Ç–µ–∫—Ü–∏–∏
        results = model(photo_path)

        # –ü—Ä–æ–≤–µ—Ä–∫–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
        if not results or len(results) == 0 or not hasattr(results[0], 'boxes'):
            await update.message.reply_text('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–∞—Ä—É–∂–∏—Ç—å –æ–±—ä–µ–∫—Ç—ã.')
            return

        # –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ –¥–µ—Ç–µ–∫—Ü–∏–π
        detections = results[0].boxes
        response = []

        for detection in detections:
            confidence = detection.conf[0]  # –ü–æ–ª—É—á–∞–µ–º —É–≤–µ—Ä–µ–Ω–Ω–æ—Å—Ç—å –¥–µ—Ç–µ–∫—Ü–∏–∏
            if confidence < CONFIDENCE_THRESHOLD:
                continue  # –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –æ–±—ä–µ–∫—Ç—ã —Å –Ω–∏–∑–∫–æ–π —É–≤–µ—Ä–µ–Ω–Ω–æ—Å—Ç—å—é

            class_id = int(detection.cls[0])
            class_name = model.names[class_id]  # –ò—Å–ø–æ–ª—å–∑—É–µ–º model.names –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∏–º–µ–Ω–∏ –∫–ª–∞—Å—Å–∞

            # –ü–µ—Ä–µ–≤–æ–¥–∏–º –∏–º—è –∫–ª–∞—Å—Å–∞ –Ω–∞ —Ä—É—Å—Å–∫–∏–π
            translated_class_name = class_translations.get(class_name, class_name)
            # –ü–æ–ª—É—á–∞–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ –Ω–∞ —Ä—É—Å—Å–∫–æ–º
            description = class_descriptions.get(translated_class_name, "–û–ø–∏—Å–∞–Ω–∏–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç.")

            response.append(f"–ù–∞–∏–º–µ–Ω–æ–≤–∞–Ω–∏–µ: {translated_class_name}\n–û–ø–∏—Å–∞–Ω–∏–µ: {description}\n–£–≤–µ—Ä–µ–Ω–Ω–æ—Å—Ç—å: {confidence:.2f}")

        if response:
            await update.message.reply_text('\n\n'.join(response))
        else:
            await update.message.reply_text('–û–±—ä–µ–∫—Ç—ã —Å –≤—ã—Å–æ–∫–æ–π —É–≤–µ—Ä–µ–Ω–Ω–æ—Å—Ç—å—é –Ω–µ –æ–±–Ω–∞—Ä—É–∂–µ–Ω—ã.')

    except Exception as e:
        await update.message.reply_text(f'–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞: {str(e)}')

